import React, { Component } from "react";
import { connect } from "react-redux";
import { getAllHistoryFromApi } from "../../Action/historyActions";
import { getProductsFromApi } from "../../Action/ProductActions";
import PorductShow from "../Modals/ModalShowProduct";
class HistoryContainer extends Component {
  componentDidMount() {
    this.props.getAllHistory();
    this.props.getallproducts();
  }

  render() {
    const { history } = this.props;
    return (
      <div>
        <select
          onChange={(e) => this.setState({ color: e.target.value })}
          name="pets"
          id="color-select"
        >
          <option value="">Couleur</option>

          <option value="Ajout produit"></option>
          <option value="suppression produit"></option>
          <option value="Modification produit"></option>
        </select>
        <table class="table">
          <thead className="headOfTableMod">
            <tr>
              <th scope="col">Nom utilisateur</th>
              <th scope="col">Nom de product</th>
              <th scope="COL">Action </th>
              <th scope="COL">Date </th>
            </tr>
          </thead>
          <tbody>
            {history.map((el, i) => (
              <tr>
                <td>{el.UserName}</td>
                <td>
                  <PorductShow product={el.product[0]} />
                </td>
                <td>{el.action}</td>
                <td>{el.Date}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  history: state.history,
  products: state.products,
});

const mapDispatchToProps = (dispatch) => ({
  getAllHistory: () => dispatch(getAllHistoryFromApi()),
  getallproducts: () => dispatch(getProductsFromApi()),
});

export default connect(mapStateToProps, mapDispatchToProps)(HistoryContainer);
