import React, { Component } from "react";
import { connect } from "react-redux";
import { getAllHistoryFromApi } from "../../Action/historyActions";
import { getProductsFromApi } from "../../Action/ProductActions";
import PorductShow from "../Modals/ModalShowProduct";
import { getUserFromApi } from "../../Action/userActions";
var newArray = [];
var Users = [];
class HistoryContainer extends Component {
  componentDidMount() {
    this.props.getAllHistory();
    this.props.getallproducts();
    this.props.getAllUsers();
    this.distinctDoubleUserNames();
  }
  state = {
    action: "",
    Users: [],
  };
  distinctDoubleUserNames = () => {
    let ArrayOfTypes = [];
    this.props.Users.map((el) => ArrayOfTypes.push(el.UserName));
    newArray = new Set(ArrayOfTypes);
    Users = [...newArray];
    console.log(Users);
  };
  render() {
    return (
      <div>
        <select
          name="pets"
          id="action-select"
          onChange={(e) => this.setState({ action: e.target.value })}
        >
          <option value="">Action</option>
          <option value="Add">Ajout</option>
          <option value="Delete">Suppression</option>
          <option value="Edit">Modification</option>
        </select>
        <select
          name="pets"
          id="action-select"
          onChange={(e) => this.setState({ action: e.target.value })}
        >
          <option value="">Action</option>
          <option value="Add">Ajout</option>
          <option value="Delete">Suppression</option>
          <option value="Edit">Modification</option>
        </select>
        <input type="date" />

        <table class="table">
          <thead className="headOfTableMod">
            <tr>
              <th scope="col">Nom utilisateur</th>
              <th scope="col">Nom de product</th>
              <th scope="COL">Action </th>
              <th scope="COL">Date </th>
            </tr>
          </thead>
          <tbody>
            {this.props.history
              .filter((elm) =>
                this.state.action === ""
                  ? elm
                  : elm.action === this.state.action
              )
              .map((el, i) => (
                <tr>
                  <td>{el.UserName}</td>
                  <td>
                    <PorductShow product={el.product[0]} />
                  </td>
                  <td>
                    {el.action === "Add"
                      ? "Ajout"
                      : el.action === "Delete"
                      ? "Suppression"
                      : "Modification"}
                  </td>
                  <td>{el.Date}</td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  history: state.history,
  products: state.products,
  users: state.users,
});

const mapDispatchToProps = (dispatch) => ({
  getAllHistory: () => dispatch(getAllHistoryFromApi()),
  getallproducts: () => dispatch(getProductsFromApi()),
  getAllUsers: () => dispatch(getUserFromApi()),
});

export default connect(mapStateToProps, mapDispatchToProps)(HistoryContainer);
