import React, { Component } from "react";
import { MDBModal } from "mdbreact";
import { connect } from "react-redux";

class ModalEditProduct extends Component {
  render() {
    return (
      <div>
        <button className="btnEditProduct" onClick={() => this.toggle()}>
          Voir le produit
        </button>
        <MDBModal
          isOpen={this.props.Modalopenshow}
          toggle={() => this.props.toggleshow()}
          fullHeight
          position="top"
        >
          <h1>{this.props.product[0].name}</h1>
        </MDBModal>
      </div>
    );
  }
}

export default connect(null, null)(ModalEditProduct);
