import React from "react";
import { Row, Col } from "react-flexbox-grid";
import Productcontainer from "./ProductContainer/productContainer";
import Mainheader from "./MainHeader/MainHeader";
import { Nav, NavItem, NavLink, Spinner } from "reactstrap";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
class Index extends React.Component {
  state = {
    loading: true,
  };
  componentDidMount() {
    setTimeout(() => {
      this.setState({ loading: false });
    }, 3000);
  }
  render() {
    return (
      <div>
        <Router>
          <div>
            <Row>
              <Col xs={12} sm={12} md={12} lg={12} className="g1">
                <Mainheader />
              </Col>
            </Row>
            <Row>
              <Col xs={2} sm={2} md={2} lg={2} className="g3">
                <div>
                  <Nav vertical>
                    <NavItem>
                      <Link>
                        {" "}
                        <Link to="/">
                          {" "}
                          <NavLink>Produits</NavLink>
                        </Link>
                      </Link>
                    </NavItem>
                    <NavItem>
                      <Link to="/users">
                        <NavLink>Mod√©rateurs</NavLink>
                      </Link>
                    </NavItem>
                    <NavItem>
                      <NavLink href="#">Historique</NavLink>
                    </NavItem>
                  </Nav>
                </div>
              </Col>
              <Col xs={10} sm={10} md={10} lg={10} className="g3">
                <Switch>
                  <Route exact path="/">
                    <Spinner color="danger" />
                    <Productcontainer />
                  </Route>
                  <Route path="/users">
                    <Productcontainer />
                  </Route>
                  <Route path="/dashboard"></Route>
                </Switch>
              </Col>
            </Row>
            <Row>
              <Col xs={12} sm={12} md={12} lg={12} className="g4">
                <div className="ui inverted  black">Footer</div>
              </Col>
            </Row>
          </div>
        </Router>
      </div>
    );
  }
}

export default Index;
