import React, { Component } from "react";
import { connect } from "react-redux";
import {
  getProductsFromApi,
  DeleteProductFromApi,
} from "../../Action/ProductActions.js";
import Addproduct from "./../Modals/ModalAddProduct";
import ProductShow from "./../Modals/ModalShowProduct";
import {
  Card,
  Button,
  CardTitle,
  CardText,
  Row,
  ButtonGroup,
  CardImg,
} from "reactstrap";
import {
  addHistoryInApi,
  getAllHistoryFromApi,
} from "../../Action/historyActions";
import ModalEditProduct from "../Modals/ModalEditProduct";

var newArray = [];
var Types = [];
var Colors = [];
var products = [];
var Brands = [];
var prduct_status = [];
var Mesures = [];
var Location = [];
var Boxs = [];
var Quantity = [];
var today = new Date();

class Productcontainer extends Component {
  state = {
    Modalopen: false,
    Modalopenshow: false,
    element: [],
    product: products,
    reference: "",
  };

  componentDidMount() {
    this.props.getAllProducts();
    this.props.getallhistory();
    console.log("tttttttt", this.props.products);
    this.setState({ products: this.props.products });
    console.log("rrrr", this.state.products);
  }
  close = () => {
    this.setState({ open: !this.state.open });
  };
  distinctDoubleType = () => {
    let ArrayOfTypes = [];
    this.props.products.map((el) => ArrayOfTypes.push(el.type));
    newArray = new Set(ArrayOfTypes);
    Types = [...newArray];
  };
  distinctDoubleColor = () => {
    let ArrayofColors = [];
    this.props.products.map((el) => ArrayofColors.push(el.color));
    newArray = new Set(ArrayofColors);
    Colors = [...newArray];
  };
  distinctDoubleBrand = () => {
    let ArrayofBrand = [];
    this.props.products.map((el) => ArrayofBrand.push(el.brand));
    newArray = new Set(ArrayofBrand);
    Brands = [...newArray];
  };
  distinctDoubleProductStatus = () => {
    let Arrayofproduct_status = [];
    this.props.products.map((el) =>
      Arrayofproduct_status.push(el.prduct_status)
    );
    newArray = new Set(Arrayofproduct_status);
    prduct_status = [...newArray];
  };
  distinctDoubleMesures = () => {
    let Arrayofmesures = [];
    this.props.products.map((el) => Arrayofmesures.push(el.mesures));
    newArray = new Set(Arrayofmesures);
    Mesures = [...newArray];
  };
  distinctDoubleLocation = () => {
    let ArrayofLocation = [];
    this.props.products.map((el) => ArrayofLocation.push(el.location));
    newArray = new Set(ArrayofLocation);
    Location = [...newArray];
  };
  distinctDoubleBox = () => {
    let ArrayofBoxs = [];

    this.props.products.map((el) => ArrayofBoxs.push(el.box));
    newArray = new Set(ArrayofBoxs);
    Boxs = [...newArray];
  };
  distinctDoubleQuantity = () => {
    let Arrayofquantity = [];

    this.props.products.map((el) => Arrayofquantity.push(el.quantity));
    newArray = new Set(Arrayofquantity);
    Quantity = [...newArray];
  };
  toggle = (el) => {
    this.setState({
      Modalopen: !this.state.Modalopen,
      element: el,
      arr: this.props.tab,
      reference: "",
    });
  };
  toggleshow = (el) => {
    this.setState({
      Modalopenshow: !this.state.Modalopenshow,
    });
  };
  filterByRef = (e) => {
    this.setState({ reference: e.target.value });
    console.log(this.state.reference);
  };
  render() {
    this.distinctDoubleType();
    this.distinctDoubleColor();
    this.distinctDoubleBrand();
    this.distinctDoubleProductStatus();
    this.distinctDoubleMesures();
    this.distinctDoubleLocation();
    this.distinctDoubleBox();
    this.distinctDoubleQuantity();

    return (
      <div>
        <Row>
          <div className="mt-5 d-flex">
            <i class="fas fa-search"></i>
            <input
              placeholder="Filtrer par reference ..."
              onChange={(e) => this.filterByRef(e)}
            />

            <Addproduct />
          </div>
        </Row>
        <Row xs={12} sm={12} md={12} lg={12}>
          <div className="ici">
            <select
              onChange={(e) => this.setState({ type: e.target.value })}
              name="pets"
              id="product-select"
            >
              <option value="">Type</option>
              {Types.map((el) => (
                <option value={el}>{el}</option>
              ))}
            </select>
            <select
              onChange={(e) => this.setState({ color: e.target.value })}
              name="pets"
              id="color-select"
            >
              <option value="">Couleur</option>
              {Colors.map((el) => (
                <option value={el}>{el}</option>
              ))}
            </select>
            <select
              onChange={(e) => this.setState({ brand: e.target.value })}
              name="pets"
              id="brand-select"
            >
              <option value="">Marque</option>
              {Brands.map((el) => (
                <option value={el}>{el}</option>
              ))}
            </select>
            <select
              onChange={(e) => this.setState({ prduct_status: e.target.value })}
              name="pets"
              id="productStatus-select"
            >
              <option value="">Produit_Status</option>
              {prduct_status.map((el) => (
                <option value={el}>{el}</option>
              ))}
            </select>
            <select
              onChange={(e) => this.setState({ mesures: e.target.value })}
              name="pets"
              id="Mesure-select"
            >
              <option value="">Mesure</option>
              {Mesures.map((el) => (
                <option value={el}>{el}</option>
              ))}
            </select>
            <select
              onChange={(e) => this.setState({ location: e.target.value })}
              name="pets"
              id="Location-select"
            >
              <option value="">Localisation</option>
              {Location.map((el) => (
                <option value={el}>{el}</option>
              ))}
            </select>
            <select
              onChange={(e) => this.setState({ box: e.target.value })}
              name="pets"
              id="box-select"
            >
              <option value="">Embalage</option>
              {Boxs.map((el) => (
                <option value={el}>{el}</option>
              ))}
            </select>
            <select
              onChange={(e) => this.setState({ quantity: e.target.value })}
              name="pets"
              id="Quantity-select"
            >
              <option value="">Quantité</option>
              {Quantity.map((el) => (
                <option value={el}>{el}</option>
              ))}
            </select>
          </div>
        </Row>
        <div className="fl">
          {this.props.products
            .filter((element) =>
              this.state.reference === ""
                ? element
                : element.reference === this.state.reference
            )
            .map((el) => (
              <a onClick={() => this.toggleshow()}>
                <Card className="cardproduct">
                  <CardImg
                    top
                    width="25px"
                    src={el.picture}
                    alt="Card image cap"
                    className="cardimageproduct"
                  />
                  <CardTitle>{el.name}</CardTitle>

                  <CardText>Réference :{el.reference}</CardText>
                  <span>Quantité:{el.quantity}</span>

                  <ButtonGroup>
                    <Button onClick={() => this.toggle(el)}>Modifier</Button>
                    <Button
                      onClick={() => {
                        this.props.DeleteProduct(el);
                        this.props.Addhistory({
                          action: "Delete",
                          UserName: localStorage.getItem("UserName"),
                          UserID: localStorage.getItem("UserID"),
                          Date:
                            today.getHours() +
                            ":" +
                            today.getMinutes() +
                            ":" +
                            today.getSeconds() +
                            "Date" +
                            today.getDate() +
                            "/" +
                            (today.getMonth() + 1) +
                            "/" +
                            today.getFullYear(),
                          product: el.id,
                        });
                      }}
                    >
                      Supprimer
                    </Button>
                  </ButtonGroup>
                </Card>
              </a>
            ))}
        </div>
        <ModalEditProduct
          Modalopen={this.state.Modalopen}
          toggle={this.toggle}
          element={this.state}
        />
        <ProductShow
          Modalopenshow={this.state.Modalopenshow}
          toggleshow={this.toggleshow}
        />
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  products: state.products,
  history: state.history,
});

const mapDispatchToProps = (dispatch) => ({
  getAllProducts: () => dispatch(getProductsFromApi()),
  getallhistory: () => dispatch(getAllHistoryFromApi()),
  DeleteProduct: (id) => dispatch(DeleteProductFromApi(id)),
  Addhistory: (element) => dispatch(addHistoryInApi(element)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Productcontainer);

