import { GET_ALL_PRODUCTS, DELETE_PRODUCT,EDIT_PRODUCT } from "./types";
import Axios from "axios";

// Get all Product from Api to Store
export const getAllproducts = (payload) => ({
  type: GET_ALL_PRODUCTS,
  payload,
});

export function getProductsFromApi() {
  return (dispatch) =>
    Axios.get("http://localhost:3000/product").then((res) =>
      dispatch(getAllproducts(res.data))
    );
}

// Delete Product from Api

export const DeleteProduct = (payload) => ({
  type: DELETE_PRODUCT,
  payload,
});

export function DeleteProductFromApi(id) {
  return (dispatch) =>
    Axios.delete("http://localhost:3000/product/" + id).then((res) =>
      dispatch(DeleteProduct(res.data), window.location.reload())
    );
}

// Edit product 

export const EditProduct=(payload)=>({
  type: EDIT_PRODUCT,
  payload,
});

export function EditProductInApi (el){
  return (dispatch) =>
  Axios.patch("http://localhost:3000/product")
}
