import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
} from "mdbreact";
import { MDBRow, MDBCol, MDBInput } from "mdbreact";
import { connect } from "react-redux";
import { addHistoryInApi } from "../../Action/historyActions";
import { EditProductInApi } from "../../Action/ProductActions";

class ModalEditProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: this.props.element.id,
      reference: this.props.element.reference,
      name: this.props.element.name,
      type: this.props.element.type,
      collection: this.props.element.collection,
      color: this.props.element.color,
      picture: this.props.element.picture,
      quantity: this.props.element.quantity,
      box: this.props.element.box,
      location: this.props.element.location,
      mesures: this.props.element.mesures,
      product_status: this.props.element.produit_status,
      brand: this.props.element.brand,
    };
  }
  render() {
    return (
      <MDBContainer>
        <MDBModal
          isOpen={this.props.Modalopen}
          toggle={() => this.props.toggle()}
          fullHeight
          position="top"
        >
          <MDBModalHeader onClick={() => this.props.toggle()}>
            Ajouter nouveaux Article
          </MDBModalHeader>
          <MDBModalBody>
            <div>
              <div>
                <MDBRow>
                  <MDBCol md="2">
                    <MDBInput
                      defaultValue={this.props.element.reference}
                      onChange={(e) =>
                        e.target.value === ""
                          ? this.setState({
                              reference: this.state.element.reference,
                            })
                          : this.setState({ reference: e.target.value })
                      }
                    ></MDBInput>
                  </MDBCol>
                  <MDBCol md="2">
                    <MDBInput
                      onChange={(e) => this.setState({ name: e.target.value })}
                      defaultValue={this.props.element.name}
                    ></MDBInput>
                  </MDBCol>
                  <MDBCol md="2">
                    <MDBInput
                      onChange={(e) => this.setState({ type: e.target.value })}
                      defaultValue={this.props.element.type}
                    ></MDBInput>
                  </MDBCol>
                  <MDBCol md="2">
                    <MDBInput
                      onChange={(e) =>
                        this.setState({ collection: e.target.value })
                      }
                      defaultValue={this.props.element.collection}
                    ></MDBInput>
                  </MDBCol>
                  <MDBCol md="2">
                    <MDBInput
                      onChange={(e) => this.setState({ brand: e.target.value })}
                      defaultValue={this.props.element.brand}
                    ></MDBInput>
                  </MDBCol>
                  <MDBCol md="2">
                    <MDBInput
                      onChange={(e) => this.setState({ color: e.target.value })}
                      defaultValue={this.props.element.color}
                    ></MDBInput>
                  </MDBCol>{" "}
                </MDBRow>
                <MDBRow>
                  <MDBCol md="2">
                    <MDBInput
                      onChange={(e) =>
                        this.setState({ product_status: e.target.value })
                      }
                      defaultValue={this.props.element.product_status}
                    ></MDBInput>
                  </MDBCol>
                  <MDBCol md="2">
                    <MDBInput
                      onChange={(e) =>
                        this.setState({ mesures: e.target.value })
                      }
                      defaultValue={this.props.element.mesures}
                    ></MDBInput>
                  </MDBCol>{" "}
                  <MDBCol md="2">
                    <MDBInput
                      onChange={(e) =>
                        this.setState({ location: e.target.value })
                      }
                      defaultValue={this.props.element.location}
                    ></MDBInput>
                  </MDBCol>{" "}
                  <MDBCol md="2">
                    <MDBInput
                      onChange={(e) => this.setState({ box: e.target.value })}
                      defaultValue={this.props.element.box}
                    ></MDBInput>
                  </MDBCol>
                  <MDBCol md="2">
                    <MDBInput
                      type="number"
                      onChange={(e) =>
                        this.setState({ quantity: e.target.value })
                      }
                      defaultValue={this.props.element.quantity}
                    ></MDBInput>
                  </MDBCol>
                  <MDBCol md="2">
                    <MDBInput
                      onChange={(e) =>
                        this.setState({ picture: e.target.value })
                      }
                      defaultValue={this.props.element.picture}
                    ></MDBInput>
                  </MDBCol>
                </MDBRow>
              </div>
            </div>
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={() => this.props.toggle()}>
              Close
            </MDBBtn>
            <MDBBtn
              color="primary"
              onClick={() => {
                console.log(this.state);
              }}
            >
              Modifier produit
            </MDBBtn>
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    );
  }
}
const mapStateToProps = (state) => ({
  user: state.user,
});

const mapDispatchToProps = (dispatch) => ({
  Addhistory: (element) => dispatch(addHistoryInApi(element)),
  EditProduct: (el) => dispatch(EditProductInApi(el)),
});

export default connect(mapStateToProps, mapDispatchToProps)(ModalEditProduct);

