import React, { Component } from "react";
import { connect } from "react-redux";
import {
  getProductsFromApi,
  DeleteProductFromApi,
} from "../../Action/ProductActions.js";

import ModalEdit from "./../Modals/EditModal";
import {
  Card,
  Button,
  CardTitle,
  CardText,
  Row,
  ButtonGroup,
  CardImg,
} from "reactstrap";
class Productcontainer extends Component {
  state = { open: false, TypesProduct: [], typedistanct: [] };

  componentDidMount() {
    this.props.getAllProducts();
  }
  close = () => {
    this.setState({ open: !this.state.open });
  };

  distinctDouble = () => {
    for (var i = 0; i < this.props.products.length; i++) {
      this.state.TypesProduct.push(this.props.products[i].type);
      console.log(this.state.TypesProduct);
    }

    /*for (var k = 0; k < this.state.TypesProduct.length; k++) {
      {
        for (var j = k + 1; k < this.state.TypesProduct.length; j++) {
          if (
            this.state.TypesProduct[k] === this.state.TypesProduct[j].length
          ) {
            console.log("existe deja");
          } else {
            this.state.typedistanct.push(this.state.TypesProduct[k]);
          }
        }
      }
    }*/
  };

  render() {
    this.distinctDouble();
    return (
      <div>
        <ModalEdit close={this.close} open={this.state.open} />

        <Row>
          <div className="ml-2 mt-2 mb-2">
            <input placeholder="Taper reference de produit" />
            <button className="ml-5">Rechercher</button>
          </div>
        </Row>
        <Row xs={12} sm={12} md={12} lg={12}>
          <select name="pets" id="product-select"></select>
        </Row>
        <div className="    fl">
          {this.props.products.map((el) => (
            <div>
              <div>
                <CardImg
                  top
                  width="25px"
                  src={el.picture}
                  alt="Card image cap"
                />
              </div>
              <div>data</div>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  products: state.products,
});

const mapDispatchToProps = (dispatch) => ({
  getAllProducts: () => dispatch(getProductsFromApi()),
  DeleteProduct: (id) => dispatch(DeleteProductFromApi(id)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Productcontainer);

