import { GET_ALL_HISTORY, ADD_HISTORY } from "./types";
import Axios from "axios";

// Get all historic from Api to Store
export const getAllHistory = (payload) => ({
  type: GET_ALL_HISTORY,
  payload,
});

export function getHistoryFromApi() {
  return (dispatch) =>
    Axios.get("http://localhost:3000/history").then((res) =>
      dispatch(getAllHistory(res.data))
    );
}

/* add  History */
export const AddHistory = (payload) => ({
  type: ADD_HISTORY,
  payload,
});

export function addHistoryInApi(data) {
  return (dispatch) =>
    Axios.post("http://localhost:3000/history", data).then((res) =>
      dispatch(AddHistory(data))
    );
}
