import React, { Component } from "react";
import { connect } from "react-redux";
import { getAllHistoryFromApi } from "../../Action/historyActions";

class HistoryContainer extends Component {
  componentDidMount() {
    this.props.getAllHistory();
  }

  render() {
    const { history } = this.props;
    return (
      <div>
        <table class="table">
          <thead>
            <tr>
              <th scope="col">Nom utilisateur</th>
              <th scope="col">Nom de product</th>
              <th scope="COL">Action </th>
              <th scope="COL">Date </th>
            </tr>
          </thead>
          <tbody>
            {history.map((el, i) => (
              <tr>
                <td>{el.UserName}</td>
                <td>ici boutton afficher la produit {el.produit[0].name}</td>
                <td>{el.action}</td>
                <td>{el.Date}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  history: state.history,
});

const mapDispatchToProps = (dispatch) => ({
  getAllHistory: () => dispatch(getAllHistoryFromApi()),
});

export default connect(mapStateToProps, mapDispatchToProps)(HistoryContainer);
