import React, { Component } from "react";
import { MDBModal } from "mdbreact";
import { connect } from "react-redux";

class ModalEditProduct extends Component {
  state = {
    Modalopen: false,
  };
  toggle = () => {
    this.setState({
      Modalopen: !this.state.Modalopen,
    });
  };
  render() {
    return (
      <div>
        <a onClick={() => this.toggle()}>Plus des informations</a>
        <MDBModal
          isOpen={this.state.Modalopen}
          toggle={() => this.toggle()}
          fullcenter
        >
          <div>
            <img
              className="mb-0"
              src={this.props.picture}
              width="250px"
              height="250px"
              alt="Card-product"
            />
            <span>id :{this.props.id}</span>
            <span>id :{this.props.id}</span>
            <span>id :{this.props.id}</span>
          </div>
        </MDBModal>
      </div>
    );
  }
}

export default connect(null, null)(ModalEditProduct);
