import React, { Component } from "react";
import { connect } from "react-redux";
import {
  getProductsFromApi,
  DeleteProductFromApi,
} from "../../Action/ProductActions.js";
import { Row, Col } from "react-flexbox-grid";
import { Navbar } from "reactstrap";
import { Button, Card, Image } from "semantic-ui-react";
import ModalEdit from "./../Modals/EditModal";
class Productcontainer extends Component {
  state = { open: false };

  close = () => {
    this.setState({ open: !this.state.open });
  };

  componentDidMount() {
    this.props.getAllProducts();
  }

  render() {
    return (
      <div>
        <ModalEdit close={this.close} open={this.state.open} />
        <Row>
          <div className="">
            <input />
          </div>
        </Row>
        <Row xs={12} sm={12} md={12} lg={12}>
          <h1>ici filter</h1>
        </Row>
        <div className="fl">
          {this.props.products.map((el) => (
            <Card>
              <Card.Content>
                <Image floated="right" size="mini" src={el.picture} />

                <Card.Meta>{el.name}</Card.Meta>
                <strong> {el.reference}</strong>
                <Card.Description>
                  <strong>{el.brand}</strong>
                </Card.Description>
                <Card.Description>
                  <strong>{el.type}</strong>
                </Card.Description>
                <Card.Description>
                  <strong>{el.prduct_status}</strong>
                </Card.Description>
                <Card.Description>
                  <strong>{el.mesures}</strong>
                </Card.Description>
                <Card.Description>
                  <strong>{el.brand}</strong>
                </Card.Description>
              </Card.Content>
              <Card.Content extra>
                <div className="ui two buttons">
                  <Button onClick={this.close} basic color="black">
                    Modifier
                  </Button>
                  <Button
                    onClick={() => this.props.DeleteProduct(el.id)}
                    basic
                    color="black"
                  >
                    Supprimer
                  </Button>
                </div>
              </Card.Content>
            </Card>
          ))}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  products: state.products,
});

const mapDispatchToProps = (dispatch) => ({
  getAllProducts: () => dispatch(getProductsFromApi()),
  DeleteProduct: (id) => dispatch(DeleteProductFromApi(id)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Productcontainer);
