import React from "react";
import { Row, Col } from "react-flexbox-grid";
import Productcontainer from "./ProductContainer/productContainer";
import Mainheader from "./MainHeader/MainHeader";
import { Nav, NavItem, NavLink, Spinner } from "reactstrap";
import { Navbar } from "reactstrap";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
class Index extends React.Component {
  state = {
    loading: true,
  };
  componentDidMount() {
    setTimeout(() => {
      this.setState({ loading: false });
    }, 1000);
  }
  render() {
    return (
      <div>
        <Router>
          <div>
            <Row className="fixed-top bg-white">
              <Col xs={12} sm={12} md={12} lg={12} className="g1">
                <Mainheader />
              </Col>
            </Row>

            <Row className="mt-5">
              <Col className="sidebar  mt-5" xs={2} sm={2} md={2} lg={2}>
                <div className="mt-5">
                  <Nav vertical className="fixed-top bg-danger ">
                    <NavItem>
                      <Link>
                        {" "}
                        <Link to="/">
                          <NavLink>Produits</NavLink>
                        </Link>
                      </Link>
                    </NavItem>
                    <NavItem>
                      <Link to="/users">
                        <NavLink>Mod√©rateurs</NavLink>
                      </Link>
                    </NavItem>
                    <NavItem>
                      <Link to="/history">
                        <NavLink>Historique</NavLink>
                      </Link>
                    </NavItem>
                  </Nav>
                </div>
              </Col>
              <Col xs={10} sm={10} md={10} lg={10} className="g3 mt-5">
                <Switch>
                  <Route exact path="/">
                    {this.state.loading ? (
                      <Spinner className="spinner" color="danger" />
                    ) : (
                      <Productcontainer />
                    )}
                  </Route>
                  <Route path="/users">
                    <h1>Ici partie utilisateurs</h1>
                  </Route>
                  <Route path="/history">
                    <h1> Ici partie Historique</h1>
                  </Route>
                </Switch>
              </Col>
            </Row>
            <Row>
              <Col xs={12} sm={12} md={12} lg={12} className="g4"></Col>
            </Row>
          </div>
        </Router>
      </div>
    );
  }
}

export default Index;
