import React, { Component } from "react";
import { connect } from "react-redux";
import {
  getProductsFromApi,
  DeleteProductFromApi,
} from "../../Action/ProductActions.js";
import { Button, Card, Image } from "semantic-ui-react";
import ModalEdit from "./../Modals/Edit_Product_Modal";
class Productcontainer extends Component {
  state = {
    ModalEdit_Open: false,
  };
  toggleEdit = () => {
    this.setState({
      ModalEdit_Open: !this.state.ModalEdit_Open,
    });
  };
  componentDidMount() {
    this.props.getAllProducts();
  }
  render() {
    return (
      <>
        <ModalEdit
          ModalEdit_Open={this.state.ModalEdit_Open}
          toggleEdit={this.toggleEdit}
        />
        <Button onClick={() => this.toggleEdit()}>dd</Button>
        <div className="fl">
          {this.props.products.map((el) => (
            <Card>
              <Card.Content>
                <Image floated="right" size="mini" src={el.picture} />

                <Card.Meta>{el.name}</Card.Meta>
                <strong> {el.reference}</strong>
                <Card.Description>
                  <strong>{el.brand}</strong>
                </Card.Description>
                <Card.Description>
                  <strong>{el.type}</strong>
                </Card.Description>
                <Card.Description>
                  <strong>{el.prduct_status}</strong>
                </Card.Description>
                <Card.Description>
                  <strong>{el.mesures}</strong>
                </Card.Description>
                <Card.Description>
                  <strong>{el.brand}</strong>
                </Card.Description>
              </Card.Content>
              <Card.Content extra>
                <div className="ui two buttons">
                  <Button basic color="green">
                    Modifier
                  </Button>
                  <Button
                    onClick={() => this.props.DeleteProduct(el.id)}
                    basic
                    color="red"
                  >
                    Supprimer
                  </Button>
                </div>
              </Card.Content>
            </Card>
          ))}
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  products: state.products,
});

const mapDispatchToProps = (dispatch) => ({
  getAllProducts: () => dispatch(getProductsFromApi()),
  DeleteProduct: (id) => dispatch(DeleteProductFromApi(id)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Productcontainer);
