import React from "react";
import { Row, Col } from "react-flexbox-grid";
import Productcontainer from "./ProductContainer/productContainer";
import Mainheader from "./MainHeader/MainHeader";
import UserContainer from "./userContainer/user";
import { Nav, NavItem, NavLink, Spinner } from "reactstrap";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

class Index extends React.Component {
  state = {
    loading: true,
  };
  componentDidMount() {
    setTimeout(() => {
      this.setState({ loading: false });
    }, 1000);
  }
  render() {
    return (
      <div>
        <Router>
          <div>
            <Row className="fixed-top bg-white">
              <Col xs={12} sm={12} md={12} lg={12} className="g1">
                <Mainheader />
              </Col>
            </Row>
            <Row className="mt-5">
              <Col className="sidebar " xs={2} sm={2} md={2} lg={2}></Col>
              <Col xs={10} sm={10} md={10} lg={8} className="g3 mt-5"></Col>
            </Row>
            <Row>
              <Col xs={12} sm={12} md={12} lg={12} className="g4">
                footer
              </Col>
            </Row>
          </div>
        </Router>
      </div>
    );
  }
}

export default Index;

