import React, { Component } from "react";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBFooter,
} from "mdbreact";
import { MDBRow, MDBCol, MDBInput } from "mdbreact";
import { connect } from "react-redux";

class ModalEditProduct extends Component {
  render() {
    return (
      <MDBContainer>
        <MDBModal
          isOpen={this.props.Modalopenshow}
          toggle={() => this.props.toggleshow()}
          fullHeight
          position="top"
        ></MDBModal>

        <Card className="cardproduct">
          <CardImg
            top
            width="25px"
            src={el.picture}
            alt="Card image cap"
            className="cardimageproduct"
          />
          <CardTitle>{el.name}</CardTitle>

          <CardText>Réference :{el.reference}</CardText>
          <span>Quantité:{el.quantity}</span>

          <ButtonGroup>
            <Button onClick={() => this.toggle(el)}>Modifier</Button>
            <Button
              onClick={() => {
                this.props.DeleteProduct(el);
                this.props.Addhistory({
                  action: "Delete",
                  UserName: localStorage.getItem("UserName"),
                  UserID: localStorage.getItem("UserID"),
                  Date:
                    today.getHours() +
                    ":" +
                    today.getMinutes() +
                    ":" +
                    today.getSeconds() +
                    "Date" +
                    today.getDate() +
                    "/" +
                    (today.getMonth() + 1) +
                    "/" +
                    today.getFullYear(),
                  product: el.id,
                });
              }}
            >
              Supprimer
            </Button>
          </ButtonGroup>
        </Card>

        <MDBFooter>
          <MDBBtn onClick={() => this.props.toggleshow()}>Fermer</MDBBtn>
        </MDBFooter>
      </MDBContainer>
    );
  }
}

export default connect(null, null)(ModalEditProduct);
