import React, { Component } from "react";

export default class MainHeaderModal extends Component {
  state = { modalOpen: false };

  handleOpen = () => this.setState({ modalOpen: true });

  handleClose = () => this.setState({ modalOpen: false });

  render() {
    return (
      <Modal
        trigger={
          <Button onClick={this.handleOpen} className="MainHeaderbtn">
            <i className="bars icon "></i>
          </Button>
        }
        open={this.state.modalOpen}
        onClose={this.handleClose}
        basic
        size="large"
      >
        <Modal.Content>
          <Button className="MainHeaderModalSignout">SIGN OUT</Button>
        </Modal.Content>
      </Modal>
    );
  }
}
