import React from "react";
import { Row, Col } from "react-flexbox-grid";
import Productcontainer from "./ProductContainer/productContainer";
import Mainheader from "./MainHeader/MainHeader";
import UserContainer from "./userContainer/user";
import { Nav, NavItem, NavLink, Spinner, Container } from "reactstrap";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import HistoryContainer from "./../components/HistoryContainer/HistoryContainer";

class Index extends React.Component {
  state = {
    loading: true,
  };
  componentDidMount() {
    setTimeout(() => {
      this.setState({ loading: false });
    }, 1000);
  }
  render() {
    return (
      <Router>
        <Container className="Container">
          <Row className=" bg-white">
            <Col xs={12} sm={12} md={12} lg={12} className="g1">
              <Mainheader />
            </Col>
          </Row>
          <Row className="mt-5">
            <Col className="sidebar p-fixed " xs={2} sm={2} md={2} lg={2}>
              <div className="mt-5 ">
                <Nav vertical>
                  <NavItem>
                    <Link>
                      <Link to="/product">
                        <NavLink>Produits</NavLink>
                      </Link>
                    </Link>
                  </NavItem>
                  <NavItem>
                    <Link to="/users">
                      <NavLink>Mod√©rateurs</NavLink>
                    </Link>
                  </NavItem>
                  <NavItem>
                    <Link to="/history">
                      <NavLink>Historique</NavLink>
                    </Link>
                  </NavItem>
                </Nav>
              </div>
            </Col>
            <Col xs={10} sm={10} md={10} lg={10} className="g3 mt-5">
              <Switch>
                <Route exact path="/product">
                  {this.state.loading ? (
                    <Spinner className="spinner" color="danger" />
                  ) : (
                    <Productcontainer />
                  )}
                </Route>
                <Route path="/users">
                  {this.state.loading ? (
                    <Spinner className="spinner" color="danger" />
                  ) : (
                    <UserContainer />
                  )}
                </Route>
                <Route path="/history">
                  <HistoryContainer />
                </Route>
              </Switch>
            </Col>
          </Row>
          <Row>
            <Col xs={12} sm={12} md={12} lg={12}>
              footer
            </Col>
          </Row>
        </Container>
      </Router>
    );
  }
}

export default Index;
