import React, { Component } from "react";
import { connect } from "react-redux";
import {
  getProductsFromApi,
  DeleteProductFromApi,
} from "../../Action/ProductActions.js";
import { Row } from "react-flexbox-grid";
import ModalEdit from "./../Modals/EditModal";
import { Card, Button, CardTitle, CardText, Col } from "reactstrap";
class Productcontainer extends Component {
  state = { open: false };

  close = () => {
    this.setState({ open: !this.state.open });
  };

  componentDidMount() {
    this.props.getAllProducts();
  }

  render() {
    return (
      <div>
        <ModalEdit close={this.close} open={this.state.open} />
        <Row>
          <div className="mt-5 ">
            <input />
          </div>
        </Row>
        <Row xs={12} sm={12} md={12} lg={12}>
          <h1>ici filter</h1>
        </Row>
        <div className="fl">
          {this.props.products.map((el) => (
            <Row>
              <Col sm="6">
                <Card body>
                  <CardTitle>Special Title Treatment</CardTitle>
                  <CardText>
                    With supporting text below as a natural lead-in to
                    additional content.
                  </CardText>
                  <Button>Go somewhere</Button>
                </Card>
              </Col>
              <Col sm="6">
                <Card body>
                  <CardTitle>Special Title Treatment</CardTitle>
                  <CardText>
                    With supporting text below as a natural lead-in to
                    additional content.
                  </CardText>
                  <Button>Go somewhere</Button>
                </Card>
              </Col>
            </Row>
          ))}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  products: state.products,
});

const mapDispatchToProps = (dispatch) => ({
  getAllProducts: () => dispatch(getProductsFromApi()),
  DeleteProduct: (id) => dispatch(DeleteProductFromApi(id)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Productcontainer);
