import React, { Component } from "react";
import { connect } from "react-redux";
import {
  getProductsFromApi,
  DeleteProductFromApi,
} from "../../Action/ProductActions.js";

import ModalEdit from "./../Modals/EditModal";
import {
  Card,
  Button,
  CardTitle,
  CardText,
  Row,
  ButtonGroup,
} from "reactstrap";
class Productcontainer extends Component {
  state = { open: false };

  close = () => {
    this.setState({ open: !this.state.open });
  };

  componentDidMount() {
    this.props.getAllProducts();
  }

  render() {
    return (
      <div>
        <ModalEdit close={this.close} open={this.state.open} />
        <Row>
          <div className="mt-5 ">
            <input />
          </div>
        </Row>
        <Row xs={12} sm={12} md={12} lg={12}>
          <h1>ici filter</h1>
        </Row>
        <div className="fl">
          {this.props.products.map((el) => (
            <Card>
              <CardImg top width="100%" src="/assets/318x180.svg" alt="Card image cap" />
              <CardTitle>{el.name}</CardTitle>

              <CardText>{el.reference}</CardText>
              <ButtonGroup>
                <Button>Modifier</Button>
                <Button>Delete</Button>
              </ButtonGroup>
            </Card>
          ))}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  products: state.products,
});

const mapDispatchToProps = (dispatch) => ({
  getAllProducts: () => dispatch(getProductsFromApi()),
  DeleteProduct: (id) => dispatch(DeleteProductFromApi(id)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Productcontainer);

