import React, { Component } from "react";
import { connect } from "react-redux";
import {
  getProductsFromApi,
  DeleteProductFromApi,
} from "../../Action/ProductActions.js";

import ModalEdit from "./../Modals/EditModal";
import {
  Card,
  Button,
  CardTitle,
  CardText,
  Row,
  ButtonGroup,
  CardImg,
} from "reactstrap";
class Productcontainer extends Component {
  state = { open: false };

  close = () => {
    this.setState({ open: !this.state.open });
  };

  componentDidMount() {
    this.props.getAllProducts();
  }

  render() {
    return (
      <div>
        <ModalEdit close={this.close} open={this.state.open} />
        <Row>
          <div className="ml-2 mt-2 mb-2">
            <input placeholder="Taper reference de produit" />
          </div>
        </Row>
        <Row xs={12} sm={12} md={12} lg={12}>
          <h1>ici filter</h1>
        </Row>
        <div>
          {this.props.products.map((el) => (
            <Card>
              <div className="d-flex">
                <div>
                  {" "}
                  <CardImg
                    top
                    width="25px"
                    src={el.picture}
                    alt="Card image cap"
                  />
                </div>
                <div>data</div>
              </div>
            </Card>
          ))}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  products: state.products,
});

const mapDispatchToProps = (dispatch) => ({
  getAllProducts: () => dispatch(getProductsFromApi()),
  DeleteProduct: (id) => dispatch(DeleteProductFromApi(id)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Productcontainer);

